import { Component, OnInit } from '@angular/core';
import { AlertifyService } from '../_services/alertify.service';
import { AuthService } from '../_services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent implements OnInit {
  model: any = {};
  constructor(private authService: AuthService,
    private alertify: AlertifyService,
    private router: Router)
  { }
  ngOnInit() {

  }
  register() {
    this.authService.register(this.model).subscribe(() => {
      this.alertify.success('Registration successful');
      this.router.navigate(['/home']);
    }, validation => {
      this.alertify.error(validation.error.errors["Password"]["0"]);
      this.alertify.error(validation.error.errors["Username"]["0"]);
    });
  }
  cancel() {
    console.log('cancelled');
  }
}
<div class="modal modal-sheet position-static d-block bg-body-secondary p-4 py-md-5">
  <div class="modal-dialog">
    <div class="modal-content rounded-4 shadow">
      <div class="modal-header p-5 pb-4 border-bottom-0">
        <h1 class="fw-bold mb-0 fs-2">Sign up</h1>
        <button type="button" class="btn-close" routerLink="home" (click)="cancel()"></button>
      </div>
      <div class="modal-body p-5 pt-0">

        <form #registerForm="ngForm" (ngSubmit)="register()">
          <div class="form-floating mb-3">
            <input type="email" class="form-control rounded-3" name="email" required [(ngModel)]="model.username" placeholder="name@example.com">
            <label>Email address</label>
          </div>
          <div class="form-floating mb-3">
            <input type="password" class="form-control rounded-3" name="password" required [(ngModel)]="model.password" placeholder="Password">
            <label>Password</label>
          </div>
          <button [disabled]="!registerForm.valid" class="w-100 mb-2 btn btn-lg rounded-3 btn-primary" type="submit">Sign up</button>

          <small class="text-body-secondary">By clicking Sign up, you agree to the terms of use.</small>
          <hr class="my-4">
          <h2 class="fs-5 fw-bold mb-3">Or use a third-party</h2>
          <button class="w-100 py-2 mb-2 btn btn-outline-secondary rounded-3" type="submit">
            <svg class="bi me-1" width="16" height="16"><use xlink:href="#twitter"></use></svg>
            Sign up with Twitter
          </button>
          <button class="w-100 py-2 mb-2 btn btn-outline-primary rounded-3" type="submit">
            <svg class="bi me-1" width="16" height="16"><use xlink:href="#facebook"></use></svg>
            Sign up with Facebook
          </button>
          <button class="w-100 py-2 mb-2 btn btn-outline-secondary rounded-3" type="submit">
            <svg class="bi me-1" width="16" height="16"><use xlink:href="#github"></use></svg>
            Sign up with GitHub
          </button>
        </form>

      </div>
    </div>
  </div>
</div>
