import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-nav-menu',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./nav-menu.component.css']
})
export class NavMenuComponent implements OnInit {
  model: any = {};

  constructor() { }

  ngOnInit() {

  }

  login() {
    console.log(this.model);
  }

}
<header class="p-3 text-bg-dark">
  <div class="container">
    <div class="d-flex flex-wrap align-items-center justify-content-center justify-content-lg-start">

      <ul class="nav col-12 col-lg-auto me-lg-auto mb-2 justify-content-center mb-md-0">
        <li><a href="#" class="nav-link px-2 text-secondary">Home</a></li>
        <li><a href="#" class="nav-link px-2 text-white">FAQs</a></li>
        <li><a href="#" class="nav-link px-2 text-white">About</a></li>
      </ul>

      <form #loginForm="ngForm" class="col-12 col-lg-auto mb-3 mb-lg-0 me-lg-3" (ngSubmit)="login()">
        <input type="text" class="form-control form-control-dark text-bg-dark loginForm" name="username" placeholder="Username" required [(ngModel)]="model.username">
        <input type="password" class="form-control form-control-dark text-bg-dark loginForm" name="password" placeholder="Password" required [(ngModel)]="model.password">
        <button type="submit" class="btn btn-outline-light me-2 loginBtn">Login</button>
        <button type="button" class="btn btn-warning loginBtn">Sign-up</button>
      </form>

    </div>
  </div>
</header>
